mapper.select:
select

from zhiyin_addr_subject_listen_trace
where
subject_id = #{subjectId}
and
subject_type = #{subjectType}
and
addr_id=#{addrId}
and
del_status=0

select

from zhiyin_addr_subject_listen_trace
where
subject_id = #{subjectId}
and
subject_type = #{subjectType}
and
addr_parent_id = #{addrParentId}
and
del_status=0

mapper.update:
update zhiyin_addr_subject_listen_trace
set del_status=1
where subject_id = #{subjectId}
and subject_type = #{subjectType}

mapper.delete:
delete from zhiyin_addr_subject_listen_trace
where
subject_id = #{subjectId}
and
subject_type = #{subjectType}
