mapper.select:
select
,

from zhiyin_content_subject_listen_cache
where subject_id = #{subjectId}
and subject_type= #{subjectType}
and del_status=0

mapper.update:
		update zhiyin_content_subject_listen_cache
   			set content_info=''
		where 
			subject_id = #{subjectId}
			and
			subject_type = #{subjectType}
